mapper.select:
SELECT count(*)
FROM ra_company
WHERE province = #{province}

    AND industry=#{industry}


    AND city=#{city}



    AND area=#{area}

select count(*)
from ra_company
where
province =#{province}
and city =#{city}
and area = #{area}

    AND industry = #{industry}

and risk_level =#{riskLevel}

SELECT (@numIndex:=@numIndex+1) numIndex,id,province,city,area,company,risk_index riskindex,risk_level
risklevel,risk_rise riskrise,rise,industry, case when area = 'æ— ' then city else concat(city,area) end registerArea
FROM ra_company c,(select @numIndex:=0 from dual) t

    c.is_black = 0

        AND c.province=#{map.province}



        AND c.city=#{map.city}



        AND c.area=#{map.area}



        AND c.industry=#{map.industry}



        AND c.risk_level=#{map.riskLevel}


ORDER BY ${map.order} ${map.descAsc}
